package com.wendaoren.web.util;

import org.springframework.util.Assert;

/**
 * @date 2019年8月27日
 * @author jonlu
 */
public abstract class SupportDefaultErrorPageTemplate {

	private static final StringBuilder TEMPLATE = new StringBuilder();
	private static final StringBuilder TEMPLATE2 = new StringBuilder();
	private static final StringBuilder TEMPLATE3 = new StringBuilder();
	
	static {
		TEMPLATE.append("<html lang=\"en\">")
		.append("<head><meta charset=\"UTF-8\">")
		.append("<meta name=\"viewport\"content=\"width=device-width, user-scalable=no, initial-scale=1.0, maximum-scale=1.0, minimum-scale=1.0\">")
		.append("<meta name=\"format-detection\"content=\"telephone=no\">")
		.append("<title>错误提示</title>")
		.append("<style>")
		.append("*{margin:0;padding:0;text-decoration:none}")
		.append("body{background:#fff;font-family:PingFangSC-Regular,Helvetica}")
		.append(".error img,.error p,.redirect p{margin:0 auto}")
		.append(".error{padding-top:180px}")
		.append(".error img{width:120px;height:48px;display:block}")
		.append(".error p{width:90%;padding:10px 15px;font-size:16px;line-height:24px;color:#adadae;text-align:center}")
		.append(".redirect p{width:90%;padding:8px 15px;font-size:12px;line-height:18px;color:#adadae;text-align:center}")
		.append(".redirect>p>a{font-weight:500;color:#6a6d6e}")
		.append("</style>")
		.append("<script type=\"text/javascript\">")
		.append("var timer=3;")
		.append("var url='");
		TEMPLATE2.append("';")
		.append("function setTimer(timer){")
		.append("document.getElementById(\"timer\").innerHTML=timer;")
		.append("}")
		.append("function redirectTo(){")
		.append("window.top.location.href=url;")
		.append("}")
		.append("function init(){")
		.append("setTimer(timer);")
		.append("if(url&&url.length>0){")
		.append("document.getElementById(\"redirect\").style.display='block';")
		.append("var interval=setInterval(function(){--timer;setTimer(timer);if(timer<=0){clearInterval(interval);redirectTo()}},1000);")
		.append("}")
		.append("}")
		.append("</script>")
		.append("</head>")
		.append("<body onload=\"init()\">")
		.append("<div class=\"error\">")
		.append("<img src=\"data:image/png;base64,\">")
		.append("<p>");
		TEMPLATE3.append("</p>")
		.append("</div>")
		.append("<div class=\"redirect\" id=\"redirect\" style=\"display:none;\">")
		.append("<p>")
		.append("<span><font color=\"red\"id=\"timer\">3</font></span>秒后<a href=\"#\"onclick=\"redirectTo()\">跳转</a>")
		.append("</p>")
		.append("</div>")
		.append("</body>")
		.append("</html>");
	}
	
	public static String getPage(String message, String redirectUrl) {
		Assert.hasText(message, "parameter message nust not empty.");
		return new StringBuilder()
				.append(TEMPLATE)
				.append((redirectUrl != null && redirectUrl.trim().length() > 0) ? redirectUrl.trim() : "")
				.append(TEMPLATE2)
				.append(message)
				.append(TEMPLATE3).toString();
	}

}